// Get the query from Parse_Brain_Response actions
const actions = $node['Parse_Brain_Response'].json.actions || [];
const newsAction = actions.find(a => a.type === 'fetch_news_rss');
const query = newsAction?.query || 'news';

// Get all RSS items
const items = $input.all();

// Filter by last 60 minutes and sort by date
const now = new Date();
const sixtyMinutesAgo = new Date(now.getTime() - 60 * 60 * 1000);

const recentNews = items
  .filter(item => {
    const pubDate = new Date(item.json.pubDate || item.json.isoDate);
    return pubDate >= sixtyMinutesAgo;
  })
  .sort((a, b) => {
    const dateA = new Date(a.json.pubDate || a.json.isoDate);
    const dateB = new Date(b.json.pubDate || b.json.isoDate);
    return dateB - dateA;
  })
  .slice(0, 3);

// Format news block
const newsBlock = recentNews.map(item => {
  const title = item.json.title || 'No title';
  const summary = item.json.contentSnippet || item.json.description || 'No summary';
  const source = item.json.creator || item.json.author || 'Unknown source';
  return `üî• ${title}\n${summary}\nüì∞ ${source}`;
}).join('\n\n');

return [{
  json: {
    news_block: newsBlock || 'ÊöÇÊó†ÊúÄÊñ∞Êñ∞Èóª',
    query: query,
    count: recentNews.length
  }
}];

